import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';
import InAppNotifications from './InAppNotifications';

const PRIORITIES = ['Low', 'Medium', 'High'];
const STATUSES = ['In Progress', 'Completed', 'Timed Out'];

export default function TodoDashboard({ user, onLogout }) {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({
    title: '',
    description: '',
    dueDate: '',
    priority: 'Low',
    status: 'In Progress',
    collaborators: '', // comma-separated emails
  });
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState('');
  const socketRef = useRef(null);

  // Fetch tasks
  const fetchTasks = () => {
    fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/tasks`, {
      credentials: 'include',
    })
      .then(res => res.json())
      .then(setTasks);
  };

  useEffect(() => {
    fetchTasks();
    // Setup socket.io
    socketRef.current = io(process.env.REACT_APP_API_URL || 'http://localhost:5000', {
      withCredentials: true
    });
    socketRef.current.emit('join', user._id);
    socketRef.current.on('task-updated', () => {
      fetchTasks();
    });
    return () => {
      socketRef.current.disconnect();
    };
  }, [user._id]);

  // Handle form input
  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Create or update task
  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    const collaborators = form.collaborators
      .split(',')
      .map(email => email.trim())
      .filter(Boolean);
    let collaboratorIds = [];
    if (collaborators.length) {
      // Fetch user IDs for emails
      const res = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/users/by-emails`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ emails: collaborators })
      });
      const data = await res.json();
      if (Array.isArray(data)) collaboratorIds = data.map(u => u._id);
    }
    const payload = {
      ...form,
      collaborators: collaboratorIds,
    };
    const url = `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/tasks${editingId ? `/${editingId}` : ''}`;
    const method = editingId ? 'PUT' : 'POST';
    const res = await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      credentials: 'include',
      body: JSON.stringify(payload)
    });
    if (!res.ok) {
      setError('Failed to save task');
      return;
    }
    setForm({ title: '', description: '', dueDate: '', priority: 'Low', status: 'In Progress', collaborators: '' });
    setEditingId(null);
    // Notify collaborators in real-time
    const updatedTask = await res.json();
    socketRef.current.emit('task-updated', updatedTask);
    fetchTasks();
  };

  // Edit task
  const handleEdit = task => {
    setEditingId(task._id);
    setForm({
      title: task.title,
      description: task.description,
      dueDate: task.dueDate ? task.dueDate.slice(0, 10) : '',
      priority: task.priority,
      status: task.status,
      collaborators: (task.collaborators || []).map(u => u.email).join(', '),
    });
  };

  // Delete task
  const handleDelete = async id => {
    await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/tasks/${id}`, {
      method: 'DELETE',
      credentials: 'include',
    });
    socketRef.current.emit('task-updated', { owner: user._id, collaborators: [] });
    setTasks(tasks.filter(t => t._id !== id));
  };

  return (
    <>
      <InAppNotifications user={user} />
      <div className="space-y-8">
        <div className="flex flex-col items-center justify-center">
          <h2 className="text-2xl font-bold text-calm5 text-center">To-Do Dashboard</h2>
          <button onClick={onLogout} className="px-4 py-2 rounded bg-calm2 text-calm5 font-semibold hover:bg-calm3 transition mt-2">Logout</button>
        </div>
        <form onSubmit={handleSubmit} className="bg-white/90 rounded-xl shadow p-4 space-y-4 mx-auto" style={{maxWidth: '700px', minWidth: '0', marginLeft: '2.5cm', marginRight: '2.5cm'}}>
          <h3 className="text-lg font-bold text-calm5 mb-2">Create Mission</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input name="title" value={form.title} onChange={handleChange} required placeholder="Task Name" className="p-2 rounded border" />
            <input name="dueDate" value={form.dueDate} onChange={handleChange} type="date" className="p-2 rounded border" />
            <input name="collaborators" value={form.collaborators} onChange={handleChange} placeholder="Share with (comma emails)" className="p-2 rounded border col-span-1 md:col-span-2" />
          </div>
          <textarea name="description" value={form.description} onChange={handleChange} placeholder="Description" className="p-2 rounded border w-full" />
          <div className="flex gap-4">
            <select name="priority" value={form.priority} onChange={handleChange} className="p-2 rounded border">
              {PRIORITIES.map(p => <option key={p}>{p}</option>)}
            </select>
            <select name="status" value={form.status} onChange={handleChange} className="p-2 rounded border">
              {STATUSES.map(s => <option key={s}>{s}</option>)}
            </select>
          </div>
          {error && <div className="text-red-500 text-sm">{error}</div>}
          <button className="w-full py-2 rounded bg-calm3 text-calm5 font-semibold hover:bg-calm2 transition">
            {editingId ? 'Update Task' : 'Add Task'}
          </button>
        </form>
        <div className="space-y-4 mx-auto" style={{maxWidth: '700px', minWidth: '0', marginLeft: '2.5cm', marginRight: '2.5cm'}}>
          <h3 className="text-lg font-bold text-calm5 mb-2">List of My Missions</h3>
          {tasks.length === 0 && <div className="text-calm5 text-center">No tasks yet.</div>}
          {tasks.map(task => (
            <div key={task._id} className="bg-white/80 rounded-lg shadow p-4 flex flex-col md:flex-row md:items-center md:justify-between gap-2">
              <div>
                <div className="font-bold text-calm5">{task.title}</div>
                <div className="text-calm5 text-sm">Due: {task.dueDate ? new Date(task.dueDate).toLocaleDateString() : 'N/A'}</div>
                <div className="text-calm5 text-xs">{task.description}</div>
                <div className="text-xs mt-1">Priority: <span className="font-semibold">{task.priority}</span> | Status: <span className="font-semibold">{task.status}</span></div>
                <div className="text-xs mt-1">Collaborators: {(
                  (task.collaborators || [])
                    .filter(u => u.email !== user.email)
                    .map(u => u.email)
                    .join(', ') || 'None'
                )}</div>
              </div>
              <div className="flex gap-2">
                <button onClick={() => handleEdit(task)} className="px-3 py-1 rounded bg-calm2 text-calm5 hover:bg-calm3">Edit</button>
                <button onClick={() => handleDelete(task._id)} className="px-3 py-1 rounded bg-red-200 text-red-700 hover:bg-red-300">Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
